    for i in range(1, 4):
        for j in range(10):
            for k in range(10):
                if abs(j - int(pin[i]) + 1) <= 1 and k!= int(pin[i - 1]):
                    dp[i][j] = min(dp[i][j], dp[i - 1][k] + 1)
                if abs(j - int(pin[i]) - 1) <= 1 and k!= int(pin[i - 1]):
                    dp[i][j] = min(dp[i][j], dp[i - 1][k] + 1)
                if k == int(pin[i - 1]):
                    dp[i][j] = min(dp[i][j], dp[i - 1][k] + 1)
    return dp[3][int(pin[3]) - 1]

