def solve(grid, points):
    def get_ring(grid, points):
        ring = []
        for i in range(10):
            for j in range(10):
                if grid[i][j] == 'X':
                    ring.append(points[len(ring)])
        return ring

    def calculate_score(ring):
        return sum(ring)

    ring_scores = []
    for i in range(5):
        ring = get_ring(list(map(list, itertools.zip_longest(*[grid[j] for j in range(i*2, 10-i*2)]))), points)
        ring_scores.append(calculate_score(ring))

    print(sum(ring_scores))
