import heapq
import sys

def solve():
    n = int(input())
    skills = []
    for _ in range(n):
        m, s = input().split()
        skills.append((int(m), s))
    skills.sort(key=lambda x: x[0])
    pq = []
    for m, s in skills:
        heapq.heappush(pq, (m, s))
    min_time_1 = float('inf')
    min_time_2 = float('inf')
    min_time_12 = float('inf')
    for m, s in pq:
        if s == '11':
            return m
        elif s == '01':
            min_time_1 = min(min_time_1, m)
        elif s == '10':
            min_time_2 = min(min_time_2, m)
        elif s == '00':
            min_time_12 = min(min_time_12, m)
