        if members == 1:
            max_members = 1
            break
        agree = [0] * k
        disagree = [0] * k
        for j in range(n):
            if (i & (1 << j))!= 0:
                for l in range(k):
                    if opinions[j][l] == '+':
                        agree[l] += 1
                    else:
                        disagree[l] += 1
        tie = True
        for l in range(k):
            if agree[l]!= disagree[l]:
                tie = False
                break
        if tie:
            max_members = max(max_members, members)
        elif agree > disagree:
            max_members = max(max_members, members)
