def solve():
    n, k = map(int, input().split())
    opinions = [input() for _ in range(n)]
    opinions = [list(opinion) for opinion in opinions]
    max_members = 0
    for i in range(1 << n):
        members = []
        for j in range(n):
            if (i & (1 << j)):
                members.append(opinions[j])
        members = Counter(members)
        y, n = 0, 0
        for opinion in members:
            y += opinion.count('+')
            n += opinion.count('-')
        if y > n:
            members = members - Counter('-')
        elif y < n:
            members = members - Counter('+')
        if y == n:
            members = Counter()
        max_members = max(max_members, len(members) + 1)
    print(max_members)

