    q = deque()
    max_likes = [0] * n
    min_likes = [0] * n
    likes = 0
    dislikes = 0
    for i in range(n):
        if a[i] > 0:
            q.append(a[i])
            likes += 1
        else:
            dislikes += 1
        max_likes[i] = likes
        min_likes[i] = likes - dislikes
    for i in range(n - 2, -1, -1):
        if a[i] > 0:
            if a[i + 1] > 0:
                max_likes[i] = max(max_likes[i], max_likes[i + 1] + 1)
            if a[i + 1] < 0:
                min_likes[i] = min(min_likes[i], min_likes[i + 1] - 1)
        else:
            if a[i + 1] > 0:
                min_likes[i] = min(min_likes[i], min_likes[i + 1] - 1)
            if a[i + 1] < 0:
                max_likes[i] = max(max_likes[i], max_likes[i + 1] + 1)
    for like in max_likes:
        print(like, end=' ')
    print()
