import sys

def solve():
    n, s, r = map(int, input().split())
    ans = [0] * n
    ans[0] = s - r
    for i in range(1, n):
        ans[i] = (s - r) // (n - 1) + 1
    r = s - sum(ans)
    for i in range(n):
        if ans[i] == (s - r) // (n - 1) + 1:
            ans[i] += r - (s - r) % (n - 1)
            r = 0
        else:
            r -= ans[i]
    print(*ans)

