def solve():
    n = int(input())
    a = int(input())
    b = int(input())

    diff = a - b
    draws = 0
    scores = []

    if diff == 0:
        draws = n
        for _ in range(n):
            scores.append("0:0")
    elif diff < 0:
        draws = 0
        for _ in range(abs(diff)):
            scores.append("0:" + str(ceil(diff / (n - _))))
        diff = -diff
        for _ in range(n - abs(diff)):
            scores.append(str(ceil(diff / (n - _))) + ":" + str(ceil(diff / (n - _))))
    else:
        draws = 0
        for _ in range(diff):
            scores.append("0:" + str(ceil(diff / (n - _))))
        diff = diff - diff
        for _ in range(n - diff):
            scores.append(str(ceil(diff / (n - _))) + ":" + str(ceil(diff / (n - _))))

    print(draws)
    for score in scores:
        print(score)

