from sys import stdin, stdout

def solve():
    a, b = stdin.readline().strip().split()
    a_size = 0
    b_size = 0
    a_x = 0
    b_x = 0

    for char in a:
        if char == 'S':
            a_size = -1
        elif char == 'M':
            a_size = 1
            break
        elif char == 'L':
            a_size = 2
            a_x = a_x + 1
        else:
            a_x = a_x + 1

    for char in b:
        if char == 'S':
            b_size = -1
        elif char == 'M':
            b_size = 1
            break
        elif char == 'L':
            b_size = 2
            b_x = b_x + 1
        else:
            b_x = b_x + 1

    if a_size > b_size:
        stdout.write('>\n')
    elif a_size < b_size:
        stdout.write('<\n')
    else:
        if a_x > b_x:
            stdout.write('>\n')
        elif a_x < b_x:
            stdout.write('<\n')
        else:
            stdout.write('='\n')

if __name__ == '__main__':
    t = int(stdin.readline().strip())
    for _ in range(t):
        solve()
