        for i in range(n):
            moves[i][1] = list(map(int, moves[i][1]))
            for j in range(moves[i][0]):
                a[i] += moves[i][1][j]
                if a[i] > 9:
                    a[i] -= 10
                elif a[i] < 0:
                    a[i] += 10
        print(*a)

if __name__ == "__main__":
    solve()
